class Solution {    public int[] twoSum(int[] nums, int target) {        int[] r = new int[2];        for(int i = 0; i < nums.length; i++){            for(int y = i + 1; y < nums.length; y++) {                if(nums[i] + nums[y] == target) {                    r[0] = i;                    r[1] = y;                    break;                }            }        }        return r;    }}